package day1;

public class Primitive {
static int a;
static char b;
static byte c;
static boolean d;
static double e;
static float f;
static long g;
static short h;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Int default value:"+a);
		System.out.println("Char default value:"+b);
		System.out.println("Byte default value:"+c);
		System.out.println("Boolean default value:"+d);
		System.out.println("Docble default value:"+e);
		System.out.println("Float default value:"+f);
		System.out.println("Long default value:"+g);
		System.out.println("Short default value:"+h);
	}

}
